import '../icon.css';
import classes from './ItemInBasket.module.css';
import {connect} from "react-redux";


const ItemInBasket = (props) => {


 const itemInBucket = props.bucketItems.items.map((item) => <li key={item.id} >{item.count + " " + item.name}</li>)

    return(
        <div className={classes.outerContainer}>
            <div className={classes.innerContainer}>
                <h3><i className="fa fa-shopping-basket" aria-hidden="true"></i> Basket</h3>
                <h3 className="clearBasket"><i className="fa fa-trash deleteButton" aria-hidden="true"></i></h3>
            </div>
            <ul className={classes.list}>
                {itemInBucket}
            </ul>
        </div>
    )


}

const mapStateToProps = (state , ownProps) => {
    return {
        ...ownProps,
        bucketItems : state.BucketItem
    }
}


export default connect(mapStateToProps ,null )(ItemInBasket);
