import './icon.css'
import classes from './Giphy.module.css';
import {useState} from 'react';
import axios from "axios";





const Giphy = () => {

    const APIKEY =  "Peg9aT5W2QKAEjgFlyQWg6vhpDEmJbXD";

    const [seachHistory , setSearchHistroy] = useState(
        {history : []}
    )

    const [inputValue , setInputValue] = useState(
        {value : "trending"}
    );

    const onChangeHandler = (e) => {
        e.preventDefault();
            setInputValue({
                value: e.target.value})
    }

    const [responseData , setResponseData] = useState(
        {
            data : []
        }
    )

    const onSubmitHandler = (e) => {
        e.preventDefault();
        let temp = seachHistory.history;
        temp.push(inputValue.value)
        setSearchHistroy(
            {history : temp}
        )
        let url = `https://api.giphy.com/v1/gifs/search?api_key=f4ee250fb7fc4ccf88cc2260099165c8&q='+this.state.search+'&limit=25&offset=0&rating=G&lang=en`;
        let search = inputValue.value.trim();
        url = url.concat(search);
        console.log(url);

        //axios get method
        axios.get(url)
            .then((response) => {
                console.log(response.data)
                setResponseData({
                    data : response.data
                })
            }).catch((err) => {
                alert("operation unsuccessfull");
        });


        }


    return (
        <div>
        <header className={classes.header}>
            <nav>
                <a href="/reddit" className={classes.redditLinkPos}>
                    Reddit <i className="fa fa-arrow-right" aria-hidden="true"></i>
                </a>
            </nav>
            <i className="fa fa-picture-o fa-6" aria-hidden="true"></i><h1 className="App-title">Hello, Giphy!</h1>
        </header>

        <div className={classes.mainBody}>
            <div className={classes.searchBar}>
                <h1># </h1>
                <form onSubmit={onSubmitHandler}>
                    <input className={classes.inputStyle} type="text" onChange={onChangeHandler}></input>
                </form>
            </div>

            <div className={classes.resultBody}>
                <div className={classes.history}>
                    <h2>Search History</h2>
                    <ul className={classes.ulStyle}>
                        {
                            seachHistory.history.map((data) => <li key={data} className={classes.liStyle}>{data}</li>)
                        }
                    </ul>
                </div>

                <div className={classes.searchResultBody}>

                </div>
            </div>
        </div>





        </div>

    )
}

export default Giphy;
